mapper.update:
UPDATE MEETING_FLASH_MEMBER

	JOIN_STATE = '2'

WHERE TARGET_NO = #{mtNo}
AND MEFL_ID = #{userId}

mapper.insert:
 	INSERT
	INTO MEETING(
	MT_NO, MM_NO, MT_NAME, MT_CONTENT, MT_START_DATE, MT_END_DATE, MT_REGDATE, MT_MAX_COUNT, MT_CURRENT_COUNT, 
	MT_CONSTRUCTOR, MT_ADDR, MT_STATE, MT_MAP_X, MT_MAP_Y
	)
	VALUES( 
	#{mtNo}, #{mmNo}, #{mtName}, #{mtContent}, 
	#{mtStart}, #{mtEnd}, now(), #{mtMaxCount}, #{mtCurrentCount}, #{mtConstructor.userId}, #{mtAddr},               
	#{mtState}, #{mtMapX}, #{mtMapY}
	)

mapper.update:
UPDATE MEETING
MT_NAME = #{mtName} , MT_CONTENT = #{mtContent}, MT_MAX_COUNT = #{mtMaxCount}, MT_ADDR = #{mtAddr}, MT_STATE = #{mtState},
WHERE MT_NO = #{mtNo}
