mapper.select:
SELECT * FROM (SELECT inner_table.* , @ROWNUM := @ROWNUM +1 AS RNUM FROM( SELECT BD.BD_NO , U.USER_ID , U.NICKNAME , U.PROFILE_IMAGE , BD.BD_NAME , BD.BD_CONTENT , BD.BD_REGDATE , BD.BD_UPDATE , BD.BD_MM_NO , BD.BD_STATE , BD.BD_CATEGORY , BD.BD_FILE , LEFT(BD.BD_PASSWORD, 1) AS BD_PASSWORD , ( SELECT COUNT(*) FROM follow_like WHERE FL_TYPE = '1' AND FL_TARGET = BD.BD_NO ) AS LIKECOUNT , ( SELECT COUNT(*) FROM REPLY WHERE REPLY_STATE = '1' AND REPLY.BD_NO = BD.BD_NO ) AS REPLYCOUNT FROM BOARD BD, USERS U WHERE BD.BD_WRITER = U.USER_ID
AND bd.bd_category = #{map.boardCategory}
AND BD.BD_STATE = #{map.boardState}
AND BD.bd_writer = #{map.userId}
AND BD.bd_MM_No= #{map.boardMoimNo}
AND BD.BD_NAME LIKE CONCAT ('%',#{map.search.searchKeyword},'%')
AND BD.BD_CONTENT LIKE CONCAT ('%',#{map.search.searchKeyword},'%')
AND U.NICKNAME LIKE CONCAT ('%',#{map.search.searchKeyword},'%')
GROUP BY BD.BD_NO , BD.BD_NAME , BD.BD_REGDATE , BD.BD_UPDATE , BD.BD_PASSWORD ORDER BY BD.BD_NO DESC ) inner_table, (select @rownum := 0) as R )tables WHERE rnum between #{map.search.startRowNum} and #{map.search.endRowNum}

mapper.insert:
INSERT INTO 
	BOARD 
	(
	  BD_CATEGORY
	, BD_WRITER
	, BD_NAME
	, BD_CONTENT
	, BD_REGDATE
	, BD_STATE
	, BD_PASSWORD
	, BD_FILE
	
	, BD_MM_NO
	
	) 
VALUES 
	(
	  #{board.boardCategory}
	, #{board.boardWriter.userId}
	, #{board.boardName}
	, #{board.boardContent}
	, now()
	, '1'
	, #{board.boardPassword}
	, #{board.boardFile}
	
	, #{board.boardMoimNo : NUMERIC}
	
)

mapper.update:
UPDATE 
BOARD SET
   BD_NAME = #{board.boardName}
 , BD_CONTENT = #{board.boardContent}
 , BD_UPDATE = now()
 WHERE 
 BD_NO = #{board.boardNo}

UPDATE 
BOARD SET
BD_STATE = #{board.boardState}
WHERE 
BD_NO = #{board.boardNo}

mapper.select:
SELECT COUNT(*) FROM BOARD BD, USERS U WHERE BD.BD_WRITER = U.USER_ID
AND bd.bd_category = #{map.boardCategory}
AND BD.BD_STATE = #{map.boardState}
AND BD.bd_writer = #{map.userId}
AND BD.bd_MM_No= #{map.boardMoimNo}
AND BD.BD_NAME LIKE CONCAT ('%',#{map.search.searchKeyword},'%')
AND BD.BD_CONTENT LIKE CONCAT ('%',#{map.search.searchKeyword},'%')
AND U.NICKNAME LIKE CONCAT ('%',#{map.search.searchKeyword},'%')
