mapper.update:
UPDATE MEETING_FLASH_MEMBER

	JOIN_STATE 	= '2'

WHERE MEFL_ID = #{userId} 
AND TARGET_NO = #{flashNo}

UPDATE FLASH
FLASH_NAME = #{flashName}, FLASH_FILE = #{flashFile}, FLASH_CONTENT = #{flashContent}, FLASH_ADDR = #{flashAddr}, FLASH_MAX_COUNT = #{flashMaxCount}, FLASH_TIME = #{flashTime}, FLASH_INTEREST = #{flashInterest}
WHERE FLASH_NO = #{flashNo}

mapper.select:
SELECT FLASH_NO, FLASH_NAME, FLASH_CONTENT, FLASH_FILE, FLASH_CONSTRUCTOR, FLASH_REGDATE, FLASH_TIME, FLASH_CURRENT_COUNT, FLASH_MAX_COUNT, FLASH_ADDR, FLASH_STATE, FLASH_INTEREST, FLASH_MAP_X, FLASH_MAP_Y , INTEREST_NAME FROM FLASH F , INTEREST I
F.FLASH_INTEREST = I.INTEREST_NO
FLASH_INTEREST = #{searchKeyword}
FLASH_ADDR = #{searchKeyword}
AND FLASH_NAME like '%${searchKeyword}%'

  	SELECT count(*)
		FROM FLASH



		FLASH_INTEREST = #{searchKeyword}


		FLASH_ADDR = #{searchKeyword}
