mapper.update:
UPDATE MEMBER

	MM_MEMBER_ROLE = '6'

WHERE MM_MEMBER_ID = #{userId}
AND MM_NO = #{mmNo}

UPDATE moim

	mm_current_count = mm_current_count +1

WHERE mm_no = #{mmNo}

UPDATE moim

	mm_current_count = mm_current_count -1

WHERE mm_no = #{mmNo}

UPDATE MOIM
MM_NAME = #{mmName} , MM_CONTENT = #{mmContent}, MM_IMAGE = #{mmFile} , MM_MAX_COUNT = #{mmMaxCount}, MM_INTEREST = #{mmInterest}, MM_CONSTRUCTOR = #{mmConstructor.userId}, MM_ADDR = #{mmAddr}, MM_STATE = #{mmState}, MM_TYPE = #{mmType}, MM_MIN_AGE = #{mmMinAge}, MM_MAX_AGE = #{mmMaxAge}
WHERE MM_NO = #{mmNo}

mapper.select:
SELECT MM_NO, MM_NAME, MM_CONTENT, MM_IMAGE, MM_MAX_COUNT, MM_CURRENT_COUNT, MM_CONSTRUCTOR, MM_REGDATE, MM_INTEREST, MM_ADDR, MM_STATE, MM_MIN_AGE, MM_MAX_AGE, MM_TYPE , INTEREST_NAME FROM MOIM M, INTEREST I
M.mm_interest = I.INTEREST_NO
AND mm_interest = #{interestNo}
AND mm_addr = #{addr}
AND mm_name like '%${searchKeyword}%'
AND mm_addr = #{addr} AND mm_interest = #{interestNo}

select count(*)
from moim



			AND mm_interest = #{interestNo}


			AND mm_addr = #{addr}


			AND mm_name like '%${searchKeyword}%' 


			AND mm_addr = #{addr} AND mm_interest = #{interestNo}
